import React, { useEffect, useRef, useState } from "react";
import LogGraph from "../logGraph/LogGraph";
import "./welcome.css";
import { setCache } from "../../utils/cache";
import ReactLeftBarChart from "../../compoenets/ReactLeftBarChart";
import ReactLeftLineChart from "../../compoenets/ReactLeftLineChart";
import ReactRightBarChart from "../../compoenets/ReactRightBarChart";
import ReactRightLineChart from "../../compoenets/ReactRightLineChart";

export default function Detection() {
  const [number, setNumber] = useState(0);  // 当前人数
  const [crowded, setCrowded] = useState(0);  //
  const [curDensity, setCurDensity] = useState(0);
  const [curScale, setScale] = useState(0);
  const [density, setDensity] = useState(0);
  const [crowdedSource, setCrowdedSource] = useState([]);
  const [dataSource, setData] = useState([]);
  const [active, setActive] = useState(false);

  let isEnd = false;
  const AREA = 23; // 区域面积
  const ref = useRef();
  const colors = [
    ["#0066cc", "#00aacc", "#0088cc"],
    ["#ff8c00", "#ffdd00", "#ffa500"],
    ["#ff0000", "#ff8c00", "#ff4500"],
  ];
  const infos = [
    "人数正常！",
    "有人群聚集风险！",
    "人员过度密集，有踩踏风险，请及时采取措施！",
  ];
  const infoSize = ["20px", "25px", "30px"];
  const infoColors = ["white"];
  const divStyle = {
    backgroundImage: `linear-gradient(to right, ${colors[density][0]}, ${colors[density][1]},${colors[density][2]})`,
  };

  useEffect(() => {
    setCache("isGraph", true);
  }, []);

  useEffect(() => {
    ref.current = setInterval(() => {
      // 循环数据
      if (isEnd) {
        localStorage.setItem("index", 0);
      }
      let index = localStorage.getItem("index") || 0;
      const num = parseInt(dataSource[index]) || 0;
      const crowdedNum = parseInt(crowdedSource[index]) || 0;
      setNumber(num);
      setCrowded(crowdedNum);
      setCurDensity(parseFloat((num / AREA).toFixed(2)));
      setScale(parseFloat(check(crowdedNum / num).toFixed(2)));
      index = parseInt(index) + 15;
      localStorage.setItem("index", index);
    }, 500);
    /**
     * 必须清理定时器，否则渲染的会变不可控，而且速度会越来越快
     */
    return () => {
      clearInterval(ref.current);
    };
  });

  useEffect(() => {
    const curDensity = number / AREA;
    if (curDensity >= 0 && curDensity <= 5.26) {
      setDensity(0);
    }
    if (curDensity > 5.26 && curDensity <= 7.6) {
      setDensity(1);
    }
    if (curDensity > 7.6) {
      setDensity(2);
    }
  }, [number]);

  const check = (num) => {
    if (!isFinite(num)) {
      return 0;
    } else if (isNaN(num)) {
      isEnd = true;
      return 0;
    } else {
      return num;
    }
  };

  return (
    <div className="demoContainer">
      <div className={"density"}>
        <div className="video">
          <video
            width="100%"
            type="application/x-shockwave-flash"
            autoPlay
            loop
          >
            <source src={require("../../video/demo.mp4")} type="video/mp4" />
          </video>
        </div>
        <div className="bottomColorArea">
          <span style={{ color: "white" }}>当前人数：{check(number)}</span>
          <span style={{ marginLeft: "40px", color: "white" }}>当前密度：{check(curDensity)}</span>
          <span style={{ marginLeft: "40px", color: "white" }}>密集人数：{check(crowded)}</span>
          <span style={{ marginLeft: "40px", color: "white" }}>密集占比：{check(curScale)}</span>
          <div
            style={{
              // backgroundColor: colors[density],
              ...divStyle,
              color: infoColors[0],
              fontSize: infoSize[density],
              borderRadius: "15px",
            }}
            className="colorArea"
          >
            {infos[density]}
          </div>
        </div>
      </div>
      {!active && (
        <LogGraph number={number} isEnd={isEnd} active={active}></LogGraph>
      )}
      {active && (
        <div className="mainBox">
          <div className="column">
            <div className="panel">
              <h2>柱状图--密集人数</h2>
              <ReactRightBarChart />
              <div className="panel-footer"></div>
            </div>
            <div className="panel" style={{ marginTop: "13px" }}>
              <h2>折线图--密度变化</h2>
              <ReactRightLineChart
                curDensity={curDensity}
                curScale={curScale}
              />
              <div className="panel-footer"></div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
