import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import LogGraph from "../logGraph/LogGraph";
import "./detection.css";
import { setCache } from "../../utils/cache";
import PubSub from "pubsub-js";
import ReactLeftBarChart from "../../compoenets/ReactLeftBarChart";
import ReactLeftLineChart from "../../compoenets/ReactLeftLineChart";
import ReactRightBarChart from "../../compoenets/ReactRightBarChart";
import ReactRightLineChart from "../../compoenets/ReactRightLineChart";
import ReactBottomChart from "../../compoenets/React"

export default function Detection() {
  const [number, setNumber] = useState(0);
  const [crowded, setCrowded] = useState(0);
  const [curDensity, setCurDensity] = useState(0);
  const [curScale, setScale] = useState(0);
  const [density, setDensity] = useState(0);
  const [crowdedSource, setCrowdedSource] = useState([]);
  const [dataSource, setData] = useState([]);
  const [active, setActive] = useState(true);

  let isEnd = false;
  const AREA = 23; // 区域面积
  const ref = useRef();
  const colors = [
    ["#0066cc", "#00aacc", "#0088cc"],
    ["#ff8c00", "#ffdd00", "#ffa500"],
    ["#ff0000", "#ff8c00", "#ff4500"],
  ];
  const infos = [
    "人数正常！",
    "有人群聚集风险！",
    "人员过度密集，有踩踏风险，请及时采取措施！",
  ];
  const infoSize = ["20px", "25px", "30px"];
  const infoColors = ["white"];
  const divStyle = {
    backgroundImage: `linear-gradient(to right, ${colors[density][0]}, ${colors[density][1]},${colors[density][2]})`,
  };

  useEffect(() => {
    setCache("isGraph", true);
    PubSub.subscribe("isEnter", handleEnter);
    axios.get("/api/getData").then((res) => {
      const data = res.data;
      let totalDataSource = data[0].map((item) => {
        return [item];
      });
      let crowdedDataSource = data[1].map((item) => {
        return [item];
      });
      setData(totalDataSource);
      setCrowdedSource(crowdedDataSource);
    });
    return () => {
      setCache("isGraph", false);
    };
  }, []);

  useEffect(() => {
    ref.current = setInterval(() => {
      // 循环数据
      if (isEnd) {
        localStorage.setItem("index", 0);
      }
      let index = localStorage.getItem("index") || 0;
      const num = parseInt(dataSource[index]) || 0;
      const crowdedNum = parseInt(crowdedSource[index]) || 0;
      setNumber(num);
      setCrowded(crowdedNum);
      setCurDensity(parseFloat((num / AREA).toFixed(2)));
      setScale(parseFloat(check(crowdedNum / num).toFixed(2)));
      index = parseInt(index) + 15;
      localStorage.setItem("index", index);
    }, 500);
    /**
     * 必须清理定时器，否则渲染的会变不可控，而且速度会越来越快
     */
    return () => {
      clearInterval(ref.current);
    };
  });

  useEffect(() => {
    const curDensity = number / AREA;
    if (curDensity >= 0 && curDensity <= 5.26) {
      setDensity(0);
    }
    if (curDensity > 5.26 && curDensity <= 7.6) {
      setDensity(1);
    }
    if (curDensity > 7.6) {
      setDensity(2);
    }
  }, [number]);

  const check = (num) => {
    if (!isFinite(num)) {
      return 0;
    } else if (isNaN(num)) {
      isEnd = true;
      return 0;
    } else {
      return num;
    }
  };

  const handleEnter = (msg, data) => {
    if (data) {
      setActive(true);
    } else {
      setActive(false);
    }
  };

  return (
    <div className="deteContainer">
      <div className="videobox">
        <div className={"title"}> 监 控 录 像 分 析 </div>
        <video type="application/x-shockwave-flash" autoPlay loop>
          <source src={require("../../video/demo.mp4")} type="video/mp4" />
        </video>
        <div className="panel">
          <div className="chartTitle">当 前 实 时 人 数 与 实 时 人 群 密 度</div>
          <ReactLeftBarChart />
        </div>
      </div>

      <div className="mainBox">
        <div className="column">
          <div className="panel">
            <div className="chartTitle">季 度 人 流 量 统 计</div>
            <ReactLeftBarChart />
          </div>

          <div className="panel" style={{ marginTop: "20px" }}>
            <ReactLeftLineChart
              number={number}
              crowded={crowded}
              isEnd={isEnd}
            />
          </div>
        </div>
      </div>

      <div className="mainBox">
        <div className="column">
          <div className="panel">
            <ReactRightBarChart />
          </div>

          <div className="panel" style={{ marginTop: "20px" }}>
            <ReactRightLineChart curDensity={curDensity} curScale={curScale} />
          </div>
        </div>
      </div>
    </div>
  );
}
