import { Breadcrumb, Layout, Menu } from "antd";
import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import "./index.css";
import MainBox from "../mainBox/MainBox";
import { getCache, setCache } from "../../utils/cache";
import PubSub from "pubsub-js";
import { showTime } from "../../utils/showTime";
import FullScreenImage from "../bgiCover/bgiCover";

const { Header, Content, Footer } = Layout;
const items = [
  {
    key: "/welcome",
    label: "主页",
  },
  {
    key: "/detection",
    label: "密度监测",
  },
];

export default function MainRouter() {
  const navigate = useNavigate();
  const [item, setItem] = useState({ key: "/welcome", label: "欢迎" });
  const SYSTEM_NAME = "";
  const isGraph = getCache("isGraph");
  const [stopMouseLeave, setStopMouseLeave] = useState(false);

  useEffect(() => {
    if (item.key !== "/detection") {
      setCache("isGraph", false);
    }
    navigate(item.key);
  }, [item, navigate, isGraph]);

  const handleClick = (idx) => {
    setItem(idx);
    navigate(idx.key, { replace: false });
  };

  const findLabel = (key) => {
    for (const item of items) {
      if (item.key === key) {
        return item.label;
      }
    }
    return "";
  };

  const handleMouseClick = () => {
    if (stopMouseLeave) {
      handleMouse(false);
      // 启用鼠标移入
      setStopMouseLeave(false);
    } else {
      handleMouse(true);
      // 禁用鼠标移入
      setStopMouseLeave(true);
    }
  };

  const handleMouseEnter = () => {
    handleMouse(true);
  };

  const handleMouseLeave = () => {
    handleMouse(false);
  };

  return (
    <div>
      <FullScreenImage />
      <Layout className={"active"}>
        <Header
          style={{
            position: "fixed",
            width: "100%",
          }}
        >
          <div className="logo" />
          <Menu
            theme="dark"
            mode="horizontal"
            defaultSelectedKeys={item.key}
            selectedKeys={item.key}
            onClick={(idx) => handleClick(idx)}
            items={items}
          />
          <div
            style={{
              color: "white",
              margin: "0 auto",
              width: "550px",
              textAlign: "center",
              marginTop: "-64px",
              fontWeight: "bold",
              fontSize: "22px",
            }}
          >
            “集”中生智——密集人群态势智能感知平台
          </div>
        </Header>
        <Content
          className={isEnter ? "site-layout-active" : "site-layout"}
          style={{
            padding: "0 50px",
            marginTop: 64,
          }}
        >
          {!isEnter && (
            <Breadcrumb
              style={{
                margin: "16px 0",
              }}
            >
              <Breadcrumb.Item>App</Breadcrumb.Item>
              <Breadcrumb.Item>{findLabel(item.key)}</Breadcrumb.Item>
            </Breadcrumb>
          )}
          <div
            className="site-layout-background"
            style={{
              padding: 24,
              minHeight: 380,
              marginTop: 30,
            }}
          >
            <MainBox item={item} />
          </div>
        </Content>
        {!isEnter && (
          <Footer
            style={{
              textAlign: "center",
              letterSpacing: "2px",
              color: "#aaa",
            }}
          >
            {SYSTEM_NAME}
          </Footer>
        )}
      </Layout>
    </div>
  );
}
