import React, { useState, useEffect } from "react";
import ReactECharts from "echarts-for-react";
export default function ReactLeftBarChart(props) {
  const DEFAULT_OPTION = {
    color: ["#2f89cf"],
    title: {
      text: "季度人流量统计",
      textStyle: {
        color: "#fff",
      },
    },
    tooltip: {
      trigger: "axis",
      axisPointer: {
        // 坐标轴指示器，坐标轴触发有效
        type: "shadow", // 默认为直线，可选为：'line' | 'shadow'
      },
    },
    grid: {
      top: 60,
      left: 30,
      right: 30,
      bottom: 30,
    },
    xAxis: [
      {
        type: "category",
        data: ["3月", "6月", "9月", "12月"],
        axisTick: {
          alignWithLabel: true,
        },
        // 修改刻度标签
        axisLabel: {
          color: "rgba(255, 255, 255, .6)",
          fontSize: "12",
        },
        axisLine: {
          show: false,
        },
      },
    ],
    yAxis: [
      {
        type: "value",
        axisLabel: {
          color: "rgba(255, 255, 255, .6)",
          fontSize: "12",
        },
        // y轴本身的线
        axisLine: {
          lineStyle: {
            color: "rgba(255,255,255,.1)",
            width: 2,
            type: "solid",
          },
        },
        // y轴分割线
        splitLine: {
          lineStyle: {
            color: "rgba(255,255,255,.1)",
          },
        },
      },
    ],
    series: [
      {
        name: "直接访问",
        type: "bar",
        barWidth: "35%",
        data: [100, 130, 180, 120],
        itemStyle: {
          barBorderRadius: 5,
        },
      },
    ],
  };

  const [option, setOption] = useState(DEFAULT_OPTION);

  return (
    <ReactECharts
      option={option}
      style={{ width: 300, height: 300 }}
    />
  );
}
