import React, { useEffect, useRef, useState } from "react";
import LogGraph from "../logGraph/LogGraph";
import { setCache } from "../../utils/cache";
import ReactLeftBarChart from "../../compoenets/ReactLeftBarChart";
import ReactLeftLineChart from "../../compoenets/ReactLeftLineChart";
import ReactRightBarChart from "../../compoenets/ReactRightBarChart";
import ReactRightLineChart from "../../compoenets/ReactRightLineChart";
import "./welcome.css";

export default function Detection() {
  const [number, setNumber] = useState(184); // 当前人数
  const [curDensity, setCurDensity] = useState(3.68); // 当前密度
  const [crowded, setCrowded] = useState(25); // 密集人数
  const [curScale, setScale] = useState(0.14); // 密集占比
  const [density, setDensity] = useState(0);
  const [crowdedSource, setCrowdedSource] = useState([25,27,29,
    26,25,
    27,32,
    39,42,
    34,51,
    51,49,
    50,47,
    44,47,
    41,38,
    35,36,
    37,37,
    36,43,
    39,40,
  42,38,
38,5,
6,7,
9,8,
10,10,
9,10,
10,16,
14,17,
17,21,
20,20,
19,30,
]);
  const [dataSource, setData] = useState([184,219,225,
    212,217,
    216,214,
    232,258,
    217,266,
    268,261,
    265,262,
    249,245,
    237,231,
    207,217,
    223,228,
    227,350,
    218,227,
  234,222,
217,101,
102,106,
112,109,
99,105,
106,105,
101,120,
112,119,
119,129,
129,124,
117,141,
134,]);
  const [active, setActive] = useState(false);
  const [index, setIndex] = useState(1);
  
  const AREA = 50; // 区域面积
  const ref = useRef();
  const colors = [
    ["#0066cc", "#00aacc", "#0088cc"],
    ["#ff8c00", "#ffdd00", "#ffa500"],
    ["#ff0000", "#ff8c00", "#ff4500"],
  ];
  const infos = [
    "人数正常！",
    "有人群聚集风险！",
    "人员过度密集，有踩踏风险，请及时采取措施！",
  ];
  const infoSize = ["1.2em", "1.3em", "1.4em"];
  const infoColors = ["white"];
  const divStyle = {
    backgroundImage: `linear-gradient(to right, ${colors[density][0]}, ${colors[density][1]},${colors[density][2]})`,
  };

  useEffect(() => {
    setCache("isGraph", true);
  }, []);

  useEffect(() => {
    ref.current = setInterval(() => {
      const num = parseInt(dataSource[index]) || 0;
      const crowdedNum = parseInt(crowdedSource[index]) || 0;
      // console.log(num);
      setNumber(num); // 当前人数
      setCrowded(crowdedNum); // 密集人数
      setCurDensity(parseFloat((num / AREA).toFixed(2))); // 当前密度
      setScale(parseFloat(check(crowdedNum / num).toFixed(2))); // 密集占比
      setIndex(parseInt(index) + 1);

    }, 500);
    /**
     * 必须清理定时器，否则渲染的会变不可控，而且速度会越来越快
     */
    return () => {
      clearInterval(ref.current);
    };
  });

  useEffect(() => {
    const curDensity = number / AREA;
    if (curDensity >= 0 && curDensity <= 5.26) {
      setDensity(0);
    }
    if (curDensity > 5.26 && curDensity <= 7.6) {
      setDensity(1);
    }
    if (curDensity > 7.6) {
      setDensity(2);
    }
  }, [number]);

  const check = (num) => {
    if (!isFinite(num)) {
      return 0;
    } else if (isNaN(num)) {
      return 0;
    } else {
      return num;
    }
  };

  return (
    <div className="welcomeContainer">
      <div className={"density"}>
        <div className={"title"}> 监 控 录 像 分 析 </div>
        <div className={"video"}>
          <video
            width="100%"
            type="application/x-shockwave-flash"
            autoPlay
            muted
            loop
          >
            <source src={require("../../video/demo.mp4")} type="video/mp4" />
          </video>
        </div>
        <div className="bottomColorArea">
          <span>当前人数：{check(number)}</span>
          <span style={{ marginLeft: "40px" }}>
            当前密度：{check(curDensity)}
          </span>
          <span style={{ marginLeft: "40px" }}>密集人数：{check(crowded)}</span>
          <span style={{ marginLeft: "40px" }}>
            密集占比：{check(curScale)}
          </span>
          <div
            style={{
              ...divStyle,
              color: infoColors[0],
              height: "40px",
              lineHeight: "40px",
              fontSize: infoSize[density],
              borderRadius: "15px",
            }}
            className="colorArea"
          >
            {infos[density]}
          </div>
        </div>
      </div>
      <LogGraph number={number} className={"graph"}></LogGraph>
    </div>
  );
}
